using (var db = new YourDataContext())\n{\n    // Assuming GetUserDetails takes no parameters\n    var userDetailsFromStoredProcedure = db.GetUserDetails().ToList();\n\n    // Now you can perform a LINQ query on the results\n    var filteredUsers = from user in userDetailsFromStoredProcedure\n                        where user.UserId > 10\n                        select new \n                        { \n                            UserId = user.UserId, \n                            UserName = user.UserName \n                        };\n\n    foreach (var user in filteredUsers)\n    {\n        Console.WriteLine($"UserId: {user.UserId}, UserName: {user.UserName}");\n    }\n}