using System;\n\nnamespace CsLibrary\n{\n    public static class ArrayProcessor\n    {\n        public static double[] ProcessArray(double[] input)\n        {\n            // Simple example modification: multiply each element by 2\n            for (int i = 0; i < input.Length; i++)\n            {\n                input[i] *= 2;\n            }\n            return input;\n        }\n    }\n}