using System.Web.Services;\nusing System.Web.Services.Protocols;\n\npublic class AuthHeader : SoapHeader\n{\n    public string UserId;\n    public string Password;\n}\n\n[WebService(Namespace = "http://yournamespace.com/")]\npublic class HrDataServive : WebService\n{\n    [WebMethod]\n    [SoapHeader("AuthHeaderValue", Required = true)]\n    public string GetData()\n    {\n        // Authentication check using AuthHeaderValue.UserId and AuthHeaderValue.Password\n        if (!AuthenticateUser(AuthHeaderValue.UserId, AuthHeaderValue.Password))\n            throw new SoapException("Unauthorized", SoapException.ClientFaultCode);\n\n        return "Data from HR System"; // Replace with actual data retrieval logic\n    }\n\n    private bool AuthenticateUser(string userId, string password)\n    {\n        // Dummy authentication function; replace with your authenticate logic\n        return userId == "admin" && password == "securePass";\n    }\n\n    public AuthHeader AuthHeaderValue;\n}