using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionStringHere";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.InfoMessage += (sender, args) => Console.WriteLine(args.Message);\n\n            SqlCommand command = new SqlCommand(\n                "PRINT 'We are here'; SELECT Name FROM Table1; SELECT Name, Country FROM Table1; PRINT 'We are done';",\n                connection);\n\n            // Enable the handling of info messages\n            command.Connection.FireInfoMessageEventOnUserErrors = true;\n\n            connection.Open();\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                do\n                {\n                    while (reader.Read())\n                    {\n                        for (int i = 0; i < reader.FieldCount; i++)\n                        {\n                            Console.Write(reader[i] + "\t");\n                        }\n                        Console.WriteLine();\n                    }\n                } while (reader.NextResult());\n            }\n        }\n    }\n}