string domainUser = filterContext.HttpContext.User.Identity.Name;\n   var parts = domainUser.Split('\\');\n   if (parts.Length == 2)\n   {\n       using (var userEntry = new DirectoryEntry("LDAP://DOMAIN", "username", "password", AuthenticationTypes.Secure))\n       {\n           var searcher = new DirectorySearcher(userEntry) { Filter = $"(sAMAccountName={parts[1]})" };\n           var result = searcher.FindOne();\n           if (result != null)\n               filterContext.Controller.ViewData["User"] = result.Properties["displayName"][0].ToString();\n       }\n   }