using (SqlConnection conn = new SqlConnection(db.Connection.ConnectionString))\n{\n    string query = "INSERT INTO tCSVFile(fileName, fileContent, mimetype, timeCreated) VALUES(@FileName, @FileContent, @MimeType, @TimeCreated)";\n    using (SqlCommand cmd = new SqlCommand(query, conn))\n    {\n        cmd.Parameters.AddWithValue("@FileName", filename);\n        cmd.Parameters.AddWithValue("@FileContent", EncodeTo64(CreateCSVFile(header, rows)));\n        cmd.Parameters.AddWithValue("@MimeType", "text/csv");\n        cmd.Parameters.AddWithValue("@TimeCreated", DateTime.Now);\n        \n        conn.Open();\n        cmd.ExecuteNonQuery();\n    }\n}