public class AddressInstantiationInterceptor : EmptyInterceptor\n{\n    public override object Instantiate(string entityName, EntityMode entityMode, object id)\n    {\n        var instance = base.Instantiate(entityName, entityMode, id);\n        \n        if (entityName == typeof(Person).FullName && instance != null)\n        {\n            var personInstance = instance as Person;\n            \n            if (personInstance != null && personInstance.Address == null)\n                personInstance.Address = new Address();\n        }\n\n        return instance;\n    }\n}