using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        var key = Encoding.UTF8.GetBytes("0123456789abcdef"); // 16-byte key\n        var iv = Encoding.UTF8.GetBytes("fedcba9876543210"); // Different from key\n\n        using (var rijndaelCipher = new RijndaelManaged())\n        {\n            rijndaelCipher.Mode = CipherMode.CBC;\n            rijndaelCipher.Key = key;\n            rijndaelCipher.IV = iv;\n            rijndaelCipher.Padding = PaddingMode.PKCS7;\n\n            try\n            {\n                using (var fsIn = new FileStream("encrypted.bin", FileMode.Open))\n                using (var decryptor = new CryptoStream(fsIn, rijndaelCipher.CreateDecryptor(), CryptoStreamMode.Read))\n                using (var srDecrypt = new StreamReader(decryptor))\n                {\n                    string fileContents = srDecrypt.ReadToEnd();\n                    Console.WriteLine(fileContents);\n                }\n            }\n            finally\n            {\n                rijndaelCipher.Clear();\n            }\n        }\n    }\n}