using System;\nusing System.IO;\n\npublic class MoneyWriter\n{\n    public static void WriteMoneyValue(BinaryWriter writer, decimal moneyValue)\n    {\n        // Convert the decimal to a numeric value that SQL Server uses internally for MONEY type\n        long internalRepresentation = (long)(moneyValue * 10000);\n\n        // Write the value as an 8-byte integer\n        writer.Write(internalRepresentation);\n    }\n\n    public static void Main()\n    {\n        using (var ms = new MemoryStream())\n        using (var bw = new BinaryWriter(ms))\n        {\n            string moneyAsString = "1234.56";\n            decimal moneyValue = decimal.Parse(moneyAsString);\n\n            WriteMoneyValue(bw, moneyValue);\n\n            // For demonstration purposes, we print the bytes\n            byte[] bytes = ms.ToArray();\n            Console.WriteLine(BitConverter.ToString(bytes));\n        }\n    }\n}