void SomeUserActionHandler(object sender, EventArgs e)\n{\n    if(Session != null && Session.IsNewSession)\n    {\n        // This might indicate an old session has expired and a new one started.\n        CleanupOldSessions();\n    }\n}\n\nvoid CleanupOldSessions()\n{\n    // Logic to remove expired sessions from the database\n    using (var db = new YourDbContext())\n    {\n        var expiredSessions = db.Sessions.Where(s => s.LastAccessed < DateTime.UtcNow.AddMinutes(-Session.Timeout));\n        db.Sessions.RemoveRange(expiredSessions);\n        db.SaveChanges();\n    }\n}