using System;\nusing System.IO;\nusing System.Web.UI;\n\npublic class SpaceIndentHtmlTextWriter : HtmlTextWriter\n{\n    // Define the number of spaces for each indentation level\n    private const int IndentationSize = 4;\n\n    public SpaceIndentHtmlTextWriter(TextWriter writer)\n        : base(writer)\n    {\n        this.Indent = 0;\n    }\n\n    protected override void OutputTabs()\n    {\n        if (this.Indent <= 0) return;\n\n        for (int i = 0; i < IndentationSize * this.Indent; i++)\n        {\n            Write(" ");\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        using (StringWriter stringOutput = new StringWriter())\n        {\n            SpaceIndentHtmlTextWriter htmlWriter = new SpaceIndentHtmlTextWriter(stringOutput);\n\n            htmlWriter.WriteFullBeginTag("html");\n            htmlWriter.WriteLine();\n            htmlWriter.WriteFullBeginTag("body");\n            htmlWriter.WriteLine();\n            htmlWriter.Indent++;\n            htmlWriter.WriteFullBeginTag("div");\n            htmlWriter.WriteLine();\n            htmlWriter.Indent++;\n            htmlWriter.WriteFullBeginTag("p");\n            htmlWriter.Write("foo");\n            htmlWriter.WriteEndTag("p");\n            htmlWriter.Indent--;\n            htmlWriter.WriteLine();\n            htmlWriter.WriteEndTag("div");\n            htmlWriter.Indent--;\n            htmlWriter.WriteLine();\n            htmlWriter.WriteEndTag("body");\n            htmlWriter.WriteLine();\n            htmlWriter.WriteEndTag("html");\n\n            Console.WriteLine(stringOutput.ToString());\n        }\n    }\n}