using NUnit.Framework;\n\n[TestFixture]\npublic class AssertTests\n{\n    [Test]\n    public void TestAreNotEqual()\n    {\n        string x = "hello";\n        string y = "world";\n\n        Assert.AreNotEqual(x, y); // This will pass as the content is different.\n    }\n\n    [Test]\n    public void TestAreNotSame()\n    {\n        object a = new object();\n        object b = new object();\n\n        Assert.AreNotSame(a, b); // This will pass as both object references are different.\n\n        object c = a;\n\n        Assert.AreNotSame(a, c); // This will fail as both variables point to the same object.\n    }\n}