using System;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionString";\n        \n        XmlDocument doc = new XmlDocument();\n        doc.Load("rules.xml"); // Load XML file with rules\n        \n        foreach (XmlNode ruleNode in doc.SelectNodes("//Rule"))\n        {\n            string tableName = ruleNode.SelectSingleNode("Table")?.InnerText;\n            string condition = ruleNode.SelectSingleNode("Condition")?.InnerText;\n\n            using (SqlConnection conn = new SqlConnection(connectionString))\n            {\n                SqlCommand cmd = new SqlCommand($"SELECT * FROM {tableName} WHERE {condition}", conn);\n                \n                try\n                {\n                    conn.Open();\n                    SqlDataReader reader = cmd.ExecuteReader();\n\n                    while (reader.Read())\n                    {\n                        Console.WriteLine("Rule Violation: " + tableName);\n                        for (int i = 0; i < reader.FieldCount; i++)\n                        {\n                            Console.WriteLine($"{reader.GetName(i)}: {reader[i]}");\n                        }\n                    }\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine("Error executing rule: " + ex.Message);\n                }\n            }\n        }\n    }\n}