using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        bool isWindowsUpdateEnabled = IsWindowsUpdateEnabled();\n        if (!isWindowsUpdateEnabled)\n        {\n            Console.WriteLine("Windows Update is disabled. For security reasons, we recommend enabling it.");\n        }\n        else\n        {\n            Console.WriteLine("Windows Update is enabled.");\n        }\n    }\n\n    static bool IsWindowsUpdateEnabled()\n    {\n        using (RegistryKey key = Registry.LocalMachine.OpenSubKey(@"SOFTWARE\Policies\Microsoft\Windows\WindowsUpdate\AU"))\n        {\n            if (key != null)\n            {\n                object noAutoUpdateValue = key.GetValue("NoAutoUpdate");\n                if (noAutoUpdateValue != null && Convert.ToInt32(noAutoUpdateValue) == 1)\n                {\n                    return false;\n                }\n            }\n        }\n\n        // If the registry setting is not present, it could mean that Windows Update is enabled by default.\n        return true;\n    }\n}