using System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "1;2;; 3;";\n        char[] delimiter = { ';' };\n        \n        // Splitting the string and filtering out nulls and empty strings\n        string[] parts = input.Split(delimiter, StringSplitOptions.RemoveEmptyEntries);\n        \n        // Optionally, you can trim whitespace from each part if needed\n        parts = parts.Select(p => p.Trim()).Where(p => !string.IsNullOrEmpty(p)).ToArray();\n        \n        // Output the cleaned array\n        foreach (var part in parts)\n        {\n            Console.WriteLine(part);\n        }\n    }\n}