using System;\nusing Microsoft.WindowsMobile.Status;\n\npublic class DeviceInfo\n{\n    public string GetDeviceSerialNumber()\n    {\n        try\n        {\n            // This will give you the ESN on MC70 with ESN module\n            return RIL.GetSystemProperty("Device.Serial");\n        }\n        catch (Exception ex)\n        {\n            return "Error: " + ex.Message;\n        }\n    }\n\n    public string GetSimIccid()\n    {\n        try\n        {\n            // Accessing SIM ICCID requires the application to be granted appropriate permissions.\n            return SimManager.GetICCIM();\n        }\n        catch (Exception ex)\n        {\n            return "Error: " + ex.Message;\n        }\n    }\n\n    public string GetImeiNumber()\n    {\n        try\n        {\n            // Attempt to get the IMEI number, if supported by hardware and enabled in software.\n            return RIL.GetSystemProperty("Device.Imei");\n        }\n        catch (Exception ex)\n        {\n            return "Error: " + ex.Message;\n        }\n    }\n}