using System;\nusing System.Web.Services.Description;\nusing System.Web.Services.Protocols;\n\npublic class MyServiceDescriptionFormatExtension : ServiceDescriptionFormatExtension\n{\n    public override object Write(ServiceDescription writer)\n    {\n        // Here you can modify the service location in the WSDL document.\n        foreach (Service s in writer.Services)\n        {\n            foreach (Port p in s.Ports)\n            {\n                foreach (Binding b in writer.Bindings)\n                {\n                    if (b.Name == p.Binding.Name)\n                    {\n                        SoapAddressBinding binding = (SoapAddressBinding)p.Extensions.Find(typeof(SoapAddressBinding));\n                        if (binding != null)\n                        {\n                            binding.Location = "http://www.domain.com/YourService.asmx";\n                        }\n                    }\n                }\n            }\n        }\n        return base.Write(writer);\n    }\n\n    // Implement other required methods and properties here.\n}