using System.Net.Mail;\n\n// Define your SMTP client and message here.\nSmtpClient smtpClient = new SmtpClient("smtp.yourserver.com");\nMailMessage mailMessage = new MailMessage();\n\n// Use the MailAddress constructor to specify both display name and email address.\nMailAddress toAddress = new MailAddress("\"myFirstName.O'Reilly\"@mywebsite.com", "O'Reilly, My First Name");\n\n// Add this address to your message's recipients collection.\nmailMessage.To.Add(toAddress);\n\n// Set other properties of the mail message as needed (From, Subject, Body).\nmailMessage.From = new MailAddress("from@example.com");\nmailMessage.Subject = "Test Email";\nmailMessage.Body = "This is a test email sent from a .NET application.";\n\n// Send the mail.\nsmtpClient.Send(mailMessage);