public class Product\n{\n    private decimal _price;\n    \n    public int RawPrice // This might be useful if you need raw values later (e.g., writing back to vendor format)\n    {\n        get { return Convert.ToInt32(_price * 100); }\n        set { _price = value / 100m; } // Ensure decimal literal with 'm'\n    }\n\n    public decimal Price\n    {\n        get => _price;\n        set => _price = value;\n    }\n}