using System.Windows.Controls;\nusing System.Windows;\nusing System.Windows.Input;\n\nnamespace SilverlightApp\n{\n    public partial class MainPage : UserControl\n    {\n        public MainPage()\n        {\n            InitializeComponent();\n        }\n\n        private void Tab_MouseEnter(object sender, MouseEventArgs e)\n        {\n            var textBlock = sender as TextBlock;\n            if (textBlock != null && textBlock.Tag is string url)\n            {\n                PreviewWebBrowser.Source = new System.Uri(url, System.UriKind.Absolute);\n                PreviewWebBrowser.Visibility = Visibility.Visible;\n\n                // Scale the content of the WebBrowser control, this part needs to be done within the loaded webpage\n            }\n        }\n    }\n}