using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\nnamespace ColorOverlayApp\n{\n    public partial class Form1 : Form\n    {\n        private static readonly Size ScreenSize = new Size(Screen.PrimaryScreen.Bounds.Width, Screen.PrimaryScreen.Bounds.Height);\n        \n        public Form1(string hexColorCode)\n        {\n            InitializeComponent();\n            this.FormBorderStyle = FormBorderStyle.None;\n            this.BackColor = ColorTranslator.FromHtml(hexColorCode); // Set your color here\n            this.Opacity = 0.5; // Adjust opacity as needed\n            this.TopMost = true;\n            this.Size = ScreenSize;\n            this.StartPosition = FormStartPosition.Manual;\n            this.Location = new Point(0, 0);\n        }\n\n        [STAThread]\n        static void Main(string[] args)\n        {\n            if (args.Length == 1 && args[0].StartsWith("#"))\n            {\n                Application.EnableVisualStyles();\n                Application.SetCompatibleTextRenderingDefault(false);\n                Application.Run(new Form1(args[0]));\n            }\n            else\n            {\n                MessageBox.Show("Usage: ColorOverlayApp.exe <hex_color>");\n            }\n        }\n    }\n}