public class MyController\n{\n    // Only one ViewModel as input, clear and simple.\n    public ActionResultIndex(MyViewModel viewModel)\n    {\n        if (ModelState.IsValid)\n        {\n            // Process view model data here.\n            return View(viewModel);\n        }\n        \n        return View("Error");\n    }\n}\n\n// ViewModel encapsulates the data from the view.\npublic class MyViewModel\n{\n    [Required]\n    public string Name { get; set; }\n    \n    [Range(18, 99)]\n    public int Age { get; set; }\n}