using GalaSoft.MvvmLight;\n   using System.ComponentModel.DataAnnotations;\n\n   public class UserViewModel : ObservableObject, IDataErrorInfo\n   {\n       private readonly UserViewModelValidator _validator = new();\n       private string _email;\n       private string _password;\n\n       public string Email\n       {\n           get => _email;\n           set { Set(nameof(Email), ref _email, value); }\n       }\n\n       public string Password\n       {\n           get => _password;\n           set { Set(nameof(Password), ref _password, value); }\n       }\n\n       // IDataErrorInfo Members\n       public string Error { get; private set; }  // Not implemented in this simple example\n\n       public string this[string columnName]\n       {\n           get\n           {\n               var result = _validator.Validate(this);\n               var firstFailure = result.Errors.FirstOrDefault(err => err.PropertyName == columnName);\n               return firstFailure?.ErrorMessage;\n           }\n       }\n   }