// SearchControl.ascx.cs\n\npublic event EventHandler<SearchCompleteEventArgs> SearchResultsUpdated;\n\nprotected void btnSearch_Click(object sender, EventArgs e)\n{\n    // Perform search logic here\n    var results = PerformSearch();\n\n    if (SearchResultsUpdated != null)\n    {\n        SearchResultsUpdated(this, new SearchCompleteEventArgs(results));\n    }\n}\n\n// Define a custom event args class to pass data\npublic class SearchCompleteEventArgs : EventArgs\n{\n    public List<Product> Products { get; }\n\n    public SearchCompleteEventArgs(List<Product> products)\n    {\n        Products = products;\n    }\n}