// Method to show the EditBookmark window\nprivate void ShowEditBookmarkWindow(BookmarkItem bookmarkItem)\n{\n    // Ensure no previous instances are left open\n    if (_editBookmarkWindow != null)\n    {\n        _editBookmarkWindow.Close();\n        _editBookmarkWindow = null;\n    }\n\n    // Create a new instance of WindowEditBookmark and pass the object\n    _editBookmarkWindow = new WindowEditBookmark(bookmarkItem);\n    _editBookmarkWindow.Closed += OnEditBookmarkWindowClosed;\n\n    // Show the window\n    _editBookmarkWindow.Show();\n}\n\n// Handler for when the EditBookmarkWindow is closed\nprivate void OnEditBookmarkWindowClosed(object sender, EventArgs e)\n{\n    // Cleanup or additional logic if needed\n    _editBookmarkWindow = null;\n}\n\n// Declare the variable to hold the sub-form instance\nprivate WindowEditBookmark _editBookmarkWindow;\n\n// Example usage: Call this method when you want to show the edit window\nShowEditBookmarkWindow(_MenuBookmark);