using System;\nusing System.Data.Entity;\nusing System.Linq;\n\npublic class BloggingContext : DbContext\n{\n    public DbSet<Blog> Blogs { get; set; }\n}\n\npublic class Blog\n{\n    public int BlogId { get; set; }\n    public string Name { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        using (var db = new BloggingContext())\n        {\n            // Add a blog\n            db.Blogs.Add(new Blog { Name = "Example Blog" });\n            db.SaveChanges();\n\n            // Query for a blog\n            var blog = db.Blogs.FirstOrDefault(b => b.Name == "Example Blog");\n            Console.WriteLine(blog.Name);\n        }\n    }\n}