using System;\nusing System.Collections.Generic;\nusing System.IO;\n\npublic class DirectorySearcher\n{\n    public List<string> SearchDirectory(string directoryPath, string searchPattern)\n    {\n        var results = new List<string>();\n        \n        try\n        {\n            // Recursively get all files in the specified directory matching the search pattern.\n            foreach (string file in Directory.GetFiles(directoryPath, searchPattern, SearchOption.AllDirectories))\n            {\n                results.Add(file);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error searching directory: {ex.Message}");\n        }\n\n        return results;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var searcher = new DirectorySearcher();\n        string pathToSearch = @"C:\YourDirectory";\n        string pattern = "*.txt"; // Search for all .txt files\n\n        List<string> searchResults = searcher.SearchDirectory(pathToSearch, pattern);\n        \n        foreach (string file in searchResults)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}