using System;\nusing System.Linq;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        DataTable dataTable = new DataTable();\n        // Assume dataTable is filled with your data.\n\n        var specialIds = new HashSet<int> { 1, 2 }; // IDs of items to be at the top\n\n        var orderedDataTable = dataTable.OrderBy(row => !specialIds.Contains(row.Field<int>("id")))\n                                         .ThenBy(row => row.Field<string>("column_name"))\n                                         .CopyToDataTable();\n\n        // orderedDataTable is now sorted with special rows first.\n    }\n}