using System;\nusing System.Windows.Forms;\n\npublic void HighlightAsInRed(RichTextBox richTextBox)\n{\n    string text = richTextBox.Text;\n    for (int i = 0; i < text.Length; i++)\n    {\n        if (text[i] == 'A')\n        {\n            richTextBox.Select(i, 1);\n            richTextBox.SelectionColor = System.Drawing.Color.Red;\n        }\n    }\n    richTextBox.Select(0, 0); // Reset the selection length back to zero\n}