using System;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = @"<html xmlns=""http://www.w3.org/1999/xhtml"" xml:lang=""en"" lang=""en"">\n<head>\n<title>title</title>\n</head>\n<body>\n<a href=""aaa.asp?id=1""> I want to get this text </a>\n<div>\n<h1>this is my want!!</h1>\n<b>this is my want!!!</b>\n</div>\n</body>\n</html>";\n\n        HtmlDocument doc = new HtmlDocument();\n        doc.LoadHtml(html);\n        \n        string cleanedHtml = doc.DocumentNode.InnerText.Trim();\n        Console.WriteLine(cleanedHtml);\n    }\n}