protected void Page_Init(object sender, EventArgs e)\n{\n    if (ViewState["Showings"] != null && ViewState["ShowingsCount"] != null)\n    {\n        int showingCount = (int)ViewState["ShowingsCount"];\n        List<Showing> showings = (List<Showing>)ViewState["Showings"];\n\n        for (int i = 0; i < showingCount; i++)\n        {\n            UserControl uc = (UserControl)Page.LoadControl("Controls/BookShowing.ascx");\n            uc.ID = "showing_" + (i + 1);\n            ((BookShowing)uc).SetShowing(showings[i]);\n            pnl_showings.Controls.Add(uc);\n        }\n    }\n}\n\nprotected void Button2_Click(object sender, EventArgs e)\n{\n    List<Showing> showings = cBLL.GetShowings(tenant.Id); // Adjust logic to get new showings\n    int showingCount = showings.Count;\n\n    ViewState["Showings"] = showings;\n    ViewState["ShowingsCount"] = showingCount;\n\n    for (int i = 0; i < showingCount; i++)\n    {\n        UserControl uc = (UserControl)Page.LoadControl("Controls/BookShowing.ascx");\n        uc.ID = "showing_" + (i + 1);\n        ((BookShowing)uc).SetShowing(showings[i]);\n        pnl_showings.Controls.Add(uc);\n    }\n\n    UpdatePanel1.Update();\n}\n\nprotected void btn_editShowings_Click(object sender, EventArgs e)\n{\n    // Example of accessing a dynamic control\n    var showingControl = (BookShowing)pnl_showings.FindControl("showing_1");\n    if (showingControl != null)\n    {\n        showingControl.Visible = false;\n    }\n}