using System;\nusing System.Reflection;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        var debugAttribute = Assembly.GetExecutingAssembly().GetCustomAttributes(typeof(DebuggableAttribute), false);\n        if (debugAttribute.Length > 0)\n        {\n            DebuggableAttribute attribute = (DebuggableAttribute)debugAttribute[0];\n            if (attribute.IsJITOptimizerDisabled && attribute.IsJITTrackingEnabled)\n            {\n                Console.WriteLine("Running in Debug Mode");\n            }\n            else\n            {\n                Console.WriteLine("Running in Release Mode");\n            }\n        }\n    }\n}