public void MyFunction(Object[,] obj)\n{\n    // Your existing logic here\n}\n\npublic Object[,] ConvertToListOfListsTo2DArray(List<List<Object>> listOfLists, int columnCount)\n{\n    int rowCount = listOfLists.Count;\n    Object[,] array2D = new Object[rowCount, columnCount];\n    \n    for (int i = 0; i < rowCount; i++)\n    {\n        if (listOfLists[i].Count != columnCount)\n            throw new ArgumentException("All inner lists must have the same number of elements as the specified column count.");\n        \n        for (int j = 0; j < columnCount; j++)\n        {\n            array2D[i, j] = listOfLists[i][j];\n        }\n    }\n\n    return array2D;\n}\n\n// Example usage:\nList<List<Object>> obj = new List<List<Object>>();\n// Add your data to obj\nobj.Add(new List<Object> { 1, "A", 3.14 });\nobj.Add(new List<Object> { 2, "B", 6.28 });\n\nint columnCount = 3; // Assuming you know this\n\nObject[,] convertedArray = ConvertToListOfListsTo2DArray(obj, columnCount);\nMyFunction(convertedArray);