protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Retrieve the data item from each row\n        DataRowView row = (DataRowView)e.Row.DataItem;\n\n        int aCount = Convert.ToInt32(row["ACount"]);\n        int bCount = Convert.ToInt32(row["BCount"]);\n        int dCount = Convert.ToInt32(row["DCount"]);\n\n        // Find the Label controls in the current GridView row\n        Label labelA = (Label)e.Row.FindControl("LabelA");\n        Label labelB = (Label)e.Row.FindControl("LabelB");\n        Label labelD = (Label)e.Row.FindControl("LabelD");\n\n        if (labelA != null)\n        {\n            labelA.Text = aCount < 0 ? "S" : "D";\n        }\n\n        if (labelB != null)\n        {\n            labelB.Text = bCount < 0 ? "S" : "D";\n        }\n\n        if (labelD != null)\n        {\n            labelD.Text = dCount < 0 ? "S" : "D";\n        }\n    }\n}