using System;\nusing System.IO;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to your certificate file(s)\n        string[] certFiles = new[]\n        {\n            @"C:\Path\To\Cert1.pfx",\n            @"C:\Path\To\Cert2.pfx"\n            // Add more certificate paths as needed\n        };\n\n        // Password for each certificate if required, otherwise null or empty string.\n        string[] passwords = new[]\n        {\n            "password1",\n            "password2"\n            // Ensure this matches the number of certificates and their respective passwords\n        };\n\n        foreach (var i in Enumerable.Range(0, certFiles.Length))\n        {\n            InstallCertificate(certFiles[i], passwords[i]);\n        }\n    }\n\n    private static void InstallCertificate(string certFilePath, string password)\n    {\n        try\n        {\n            // Loads the certificate file.\n            var certificate = new X509Certificate2(certFilePath, password);\n\n            // Open the Local User Personal Certificate Store (My).\n            using (var store = new X509Store(StoreName.My, StoreLocation.CurrentUser))\n            {\n                store.Open(OpenFlags.ReadWrite);\n                store.Add(certificate);\n                Console.WriteLine($"Installed certificate: {certificate.FriendlyName}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to install certificate from {certFilePath}: {ex.Message}");\n        }\n    }\n}