using System;\nusing Microsoft.Win32;\nusing System.IO;\n\nclass Program {\n    static void Main() {\n        string localAppDataPath = Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData);\n        string roamingAppDataPath = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData);\n\n        // Example of deciding where to store data\n        bool shouldRoam = true; // Set based on your application's needs\n        string chosenPath = shouldRoam ? roamingAppDataPath : localAppDataPath;\n\n        Console.WriteLine("Chosen Path: " + chosenPath);\n    }\n}