using System;\nusing System.Diagnostics;\nusing Newtonsoft.Json;\n\npublic class CustomJsonTraceListener : TraceListener\n{\n    public override void Write(string message)\n    {\n        // You can extend this method if needed.\n    }\n\n    public override void WriteLine(string message)\n    {\n        Console.WriteLine(message); // Or log to a file/database etc.\n    }\n    \n    public virtual void Log<T>(T data)\n    {\n        string serializedData = JsonConvert.SerializeObject(data);\n        WriteLine(serializedData);\n    }\n}\n\n// Usage in your application\npublic class Program\n{\n    static void Main()\n    {\n        Trace.Listeners.Add(new CustomJsonTraceListener());\n        \n        var eventData = new { EventName = "ApplicationStarted", Timestamp = DateTime.Now };\n        ((CustomJsonTraceListener)Trace.Listeners[0]).Log(eventData);\n        \n        Trace.Flush();\n        Trace.Close();\n    }\n}