using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("user32.dll")]\n    static extern void keybd_event(byte bVk, byte bScan, uint dwFlags, UIntPtr dwExtraInfo);\n\n    private const int KEYEVENTF_KEYUP = 0x0002;\n    private const byte VK_CONTROL = 0x11;\n    private const byte VK_MENU = 0x12; // Alt key\n    private const byte VK_DELETE = 0x2E;\n\n    static void Main()\n    {\n        // Press Control+Alt+Delete\n        keybd_event(VK_CONTROL, 0, 0, (UIntPtr)0);\n        keybd_event(VK_MENU, 0, 0, (UIntPtr)0);\n        keybd_event(VK_DELETE, 0, 0, (UIntPtr)0);\n\n        // Release Control+Alt+Delete\n        keybd_event(VK_DELETE, 0, KEYEVENTF_KEYUP, (UIntPtr)0);\n        keybd_event(VK_MENU, 0, KEYEVENTF_KEYUP, (UIntPtr)0);\n        keybd_event(VK_CONTROL, 0, KEYEVENTF_KEYUP, (UIntPtr)0);\n    }\n}