using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        string progId = "YourProgId"; // CLSID or ProgID of the COM server\n        Type type = Type.GetTypeFromProgID(progId, "RemoteComputerName");\n\n        if (type != null)\n        {\n            object comObject = Activator.CreateInstance(type);\n            // Use the COM object...\n        }\n        else\n        {\n            Console.WriteLine("Type not found.");\n        }\n    }\n}