using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        int topParam = 10; // This is the value you want to use for TOP\n        string somevalue = "yourValue"; // Example other parameter\n\n        using (SqlConnection connection = new SqlConnection("YourConnectionString"))\n        {\n            string query = $"SELECT TOP {topParam} * FROM table1 WHERE SomeColumn = @somevalue";\n\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.Add("@somevalue", SqlDbType.VarChar).Value = somevalue;\n\n                connection.Open();\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        // Process each row\n                    }\n                }\n            }\n        }\n    }\n}