public class CustomerRepository : ICustomerRepository\n{\n    private readonly MyDbContext _context;\n\n    public CustomerRepository(MyDbContext context)\n    {\n        _context = context;\n    }\n\n    public MyPocoModels.Customer Get(int id)\n    {\n        var entity = _context.Customers.FirstOrDefault(c => c.ID == id);\n        return ConvertToPoco(entity);\n    }\n\n    private static MyPocoModels.Customer ConvertToPoco(Customer entity)\n    {\n        if (entity == null) return null;\n        return new MyPocoModels.Customer { ID = entity.ID, Name = entity.Name };\n    }\n}