using System;\nusing System.IO;\n\npublic class SettingsManager\n{\n    private readonly string _settingsFilePath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.CommonApplicationData), "MyApp", "Settings.txt");\n\n    public void SaveSetting(string key, string value)\n    {\n        lock (_lockObject)\n        {\n            using (var writer = new StreamWriter(_settingsFilePath, true))\n            {\n                writer.WriteLine($"{key}={value}");\n            }\n        }\n    }\n\n    public string GetSetting(string key)\n    {\n        lock (_lockObject)\n        {\n            if (File.Exists(_settingsFilePath))\n            {\n                foreach (string line in File.ReadAllLines(_settingsFilePath))\n                {\n                    var parts = line.Split('=');\n                    if (parts.Length == 2 && parts[0] == key)\n                        return parts[1];\n                }\n            }\n            return null;\n        }\n    }\n\n    private static readonly object _lockObject = new object();\n}