using Aspose.Pdf.Forms;\nusing Aspose.Pdf;\n\npublic void FillPDF(string templatePath, string outputPath, Dictionary<string, string> fieldValues)\n{\n    Document pdfDocument = new Document(templatePath);\n    PdfFormEditor formEditor = new PdfFormEditor(pdfDocument);\n\n    foreach (var kvp in fieldValues)\n    {\n        FormField field = formEditor.GetField(kvp.Key);\n        if (field != null)\n            field.Value = kvp.Value;\n    }\n\n    pdfDocument.Save(outputPath);\n}