using System;\nusing System.IO;\n\nclass DirectoryTraversalExample\n{\n    static void TraverseDirectory(string path)\n    {\n        // Get all the file system entries in the current directory.\n        foreach (var entry in new DirectoryInfo(path).GetFileSystemInfos())\n        {\n            if ((entry.Attributes & FileAttributes.ReparsePoint) != FileAttributes.ReparsePoint)\n            {\n                Console.WriteLine(entry.FullName);\n                \n                if (entry is DirectoryInfo subDirInfo)\n                {\n                    TraverseDirectory(subDirInfo.FullName); // Recursive call\n                }\n            }\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        if (args.Length == 0)\n        {\n            Console.WriteLine("Please provide a directory path.");\n            return;\n        }\n\n        string rootPath = args[0];\n\n        try\n        {\n            TraverseDirectory(rootPath);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine($"An error occurred: {e.Message}");\n        }\n    }\n}