using System;\nusing System.IO;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Assuming 'memoryStream' is your MemoryStream containing the string data.\n        using (MemoryStream memoryStream = new MemoryStream())\n        {\n            // Example: Write a string to the MemoryStream for demonstration.\n            byte[] textBytes = Encoding.UTF8.GetBytes("Hello, world!");\n            memoryStream.Write(textBytes, 0, textBytes.Length);\n            memoryStream.Position = 0; // Reset stream position before reading\n\n            using (StreamReader reader = new StreamReader(memoryStream, Encoding.UTF8))\n            {\n                string result = reader.ReadToEnd();\n                Console.WriteLine(result); // Outputs: Hello, world!\n            }\n        }\n    }\n}