using System;\nusing System.Net.Sockets;\n\npublic class AsyncSocketSender\n{\n    private Socket _socket;\n\n    public AsyncSocketSender(Socket socket)\n    {\n        _socket = socket;\n    }\n\n    public void SendPacket(byte[] data)\n    {\n        // Ensure there is data to send\n        if (data.Length == 0)\n        {\n            Console.WriteLine("No data to send.");\n            return;\n        }\n\n        try\n        {\n            // Begin sending the data\n            _socket.BeginSend(data, 0, data.Length, SocketFlags.None, new AsyncCallback(SendCallback), data);\n        }\n        catch (SocketException se)\n        {\n            Console.WriteLine($"SocketException: {se.Message}");\n        }\n    }\n\n    private void SendCallback(IAsyncResult ar)\n    {\n        try\n        {\n            // Retrieve the socket from the state object.\n            byte[] buffer = (byte[])ar.AsyncState;\n            \n            int bytesSent = _socket.EndSend(ar);\n            Console.WriteLine($"Sent {bytesSent} bytes.");\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine(e.ToString());\n        }\n    }\n}