using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("rapi.dll", CharSet = CharSet.Unicode)]\n    static extern int CeFindFirstFile(string lpFileName, out WIN32_FIND_DATA lpFindFileData);\n\n    [DllImport("rapi.dll")]\n    static extern int CeFindNextFile(int hFindFile, out WIN32_FIND_DATA lpFindFileData);\n\n    [DllImport("rapi.dll")]\n    static extern bool CeFindClose(int hFindFile);\n\n    struct WIN32_FIND_DATA\n    {\n        public uint dwFileAttributes;\n        public System.Runtime.InteropServices.ComTypes.FILETIME ftCreationTime;\n        public System.Runtime.InteropServices.ComTypes.FILETIME ftLastAccessTime;\n        public System.Runtime.InteropServices.ComTypes.FILETIME ftLastWriteTime;\n        public uint nFileSizeHigh;\n        public uint nFileSizeLow;\n        public int dwOID;\n        [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 260)]\n        public string cFileName;\n    }\n\n    static void Main()\n    {\n        WIN32_FIND_DATA data;\n        int hFind = CeFindFirstFile(@"\Storage Card\*.txt", out data);\n\n        if (hFind != -1)\n        {\n            do\n            {\n                Console.WriteLine(data.cFileName);\n            } while (CeFindNextFile(hFind, out data) == 0);\n\n            CeFindClose(hFind);\n        }\n    }\n}