using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [STAThread]\n    static void Main()\n    {\n        // Initialize the SourceSafe database connection\n        dynamic vssDatabase = Activator.CreateInstance(Type.GetTypeFromProgID("SourceSafe.Database"));\n        vssDatabase.Open(@"C:\Path\To\VSS", "Admin", "Password");\n\n        // Example: Get the root project\n        dynamic rootProject = vssDatabase.VSSItem("$/", false);\n\n        // Iterate through each item in the root\n        foreach (dynamic item in rootProject.get_Items(false))\n        {\n            Console.WriteLine($"Item: {item.Name}, IsCheckedOut: {item.IsCheckedOut}");\n\n            if (item.IsCheckedOut)\n            {\n                Console.WriteLine($"Checked out by: {item.CheckedOutBy}");\n                // Example action: Undo checkout (not shown here, requires more API calls depending on needs)\n            }\n        }\n\n        vssDatabase.Close();\n    }\n}