protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           BindGridView(); // Method to bind data to the GridView\n\n           foreach (DataControlField col in gridView.Columns)\n           {\n               if (col.HeaderText == "AdminColumn" && !User.IsInRole("Admin"))\n               {\n                   col.Visible = false;\n               }\n               else\n               {\n                   col.Visible = true;\n               }\n           }\n       }\n   }\n\n   private void BindGridView()\n   {\n       // Assuming you have a method to get data from the database and bind it to your GridView.\n       gridView.DataSource = GetData(); \n       gridView.DataBind();\n   }