// Example using BenchmarkDotNet in C#\n\n  [MemoryDiagnoser]\n  public class SortingBenchmarks\n  {\n      private readonly int[] _data;\n\n      public SortingBenchmarks()\n      {\n          _data = Enumerable.Range(0, 1000).OrderBy(x => Guid.NewGuid()).ToArray(); // random numbers\n      }\n\n      [Benchmark(Baseline = true)]\n      public void ArraySort() => Array.Sort(_data);\n\n      [Benchmark]\n      public void CustomUnsafeSort() => UnsafeMergeSortImplementation(_data);\n  }